package com.moralesce.jbehave.examples;import org.jbehave.scenario.JUnitScenario;import org.jbehave.scenario.reporters.ScenarioReporter;import org.jbehave.scenario.steps.Steps;import org.junit.runner.RunWith;import com.moralesce.jbehave.JUnitScenarioRunner;import com.moralesce.jbehave.annotations.UseConfiguration;@UseConfiguration(StoryConfiguration.class)@RunWith(JUnitScenarioRunner.class)public class Story extends JUnitScenario {    private Story() {    }    public Story(ScenarioReporter reporter, Steps... steps) {        super(new StoryConfiguration(reporter), steps);    }}